window.onscroll=function(){myFunction()};const nav=document.getElementById("navigation"),sticky=nav.offsetTop,logo=document.getElementById("logo"),parallax=document.getElementById("home"),links=nav.querySelectorAll(".navigation_item"),scroll=new SmoothScroll('a[href*="#"]',{speed:800,easing:"easeOutQuad"});function scrollFunction(){document.body.scrollTop>20||document.documentElement.scrollTop>20?document.getElementById("scrollUp").style.display="block":document.getElementById("scrollUp").style.display="none"}function myFunction(){window.pageYOffset>sticky?(nav.classList.add("sticky"),logo.style.color="#000000",scrollFunction()):(nav.classList.remove("sticky"),logo.style.color="#ffffff")}function openNav(){document.getElementById("nav_list").style.cssText="animation: open .5s forwards;"}function closeNav(){document.getElementById("nav_list").style.cssText="animation: close .5s forwards;"}window.addEventListener("scroll",function(){let e=window.pageYOffset;parallax.style.backgroundPositionY=-.5*e+"px"}),document.getElementById("nav_burger").addEventListener("click",openNav),document.getElementById("nav_cross").addEventListener("click",closeNav);